(()=>{const e=document.querySelector("input"),t=document.querySelector(".searchBtn");let n={};t.addEventListener("click",(()=>{const t=e.value;console.log(t),function(e){fetch("https://api.openweathermap.org/data/2.5/weather?q="+e+"&APPID=f267f51a8b8e85d282f5213c10ec1f47",{mode:"cors"}).then((e=>{if(e.ok)return e.json();throw new Error("Invalid city request")})).then((e=>{!function(e){n={weather:e.weather[0].main,temperature:e.main.temp,feelsLike:e.main.feels_like,tempHigh:e.main.temp_max,tempLow:e.main.temp_min}}(e)})).then((()=>{!function(){const e=1.8*(n.temperature-273.15)+32;n.temperature=Math.round(100*(e+Number.EPSILON))/100;const t=1.8*(n.feelsLike-273.15)+32;n.feelsLike=Math.round(100*(t+Number.EPSILON))/100;const r=1.8*(n.tempHigh-273.15)+32;n.tempHigh=Math.round(100*(r+Number.EPSILON))/100;const o=1.8*(n.tempLow-273.15)+32;n.tempLow=Math.round(100*(o+Number.EPSILON))/100,console.log(n)}()})).catch((function(e){alert(e)}))}(t)})),e.addEventListener("keypress",(e=>{"Enter"===e.key&&t.click()}))})();